mapper.select:
SELECT p.id AS product_id, p.name AS product_name, p.price, p.color, a.artisan_name AS artisan_name, c.category_name AS category_name
FROM hm_product p LEFT JOIN hm_artisan a ON p.artisan_id = a.id LEFT JOIN hm_category c ON p.category_id = c.id
p.category_id = #{categoryId}
AND p.artisan_id = #{artisanId}
AND LOWER(p.name) LIKE CONCAT('%', LOWER(#{search}), '%')
${orderby} ${pagination}

SELECT COUNT(*) FROM hm_product p LEFT JOIN hm_artisan a ON p.artisan_id = a.id LEFT JOIN hm_category c ON p.category_id = c.id
p.category_id = #{categoryId}
AND p.artisan_id = #{artisanId}
AND LOWER(p.name) LIKE CONCAT('%', LOWER(#{search}), '%')
